import React,{useState, useEffect} from 'react'
import {Link, useParams } from 'react-router-dom';
import SingleStudentInfo from '../components/SingleStudentInfo';
import UpdateStudents from '../components/UpdateStudents';
import {ReactComponent as ArrowLeft} from '../icons/arrow-left.svg'
import {ReactComponent as HomeIcon} from '../icons/home.svg'

const StudentPage = () => {
    const {id} = useParams()
    const [student, setStudent] = useState(null)
    const [editing, setEditing] = useState(false)

    const getStudent = async() => {
        
        const response = await fetch(`/students/${id}`)
        const data = await response.json()
        setStudent(data)
    }
    useEffect(()=>{
        getStudent()
    },[])

    const headers = ["Student's name", 'School', "Salary", 'About', 
                     'Lesson time', "Lessons days", 
                        "Unpaid teaching time", "Holydays/break"]

    function convert_date(date)
    {
        const year = date.slice(0,4)
        const month = date.slice(5,7)
        const day = date.slice(8,10)
        const oclock = date.slice(11,16 )
        return year + "." + month + "." + day + " " + oclock
    }
    const removeStudent = async() =>
    {
        const request = await fetch(`/students/${id}/remove/`,{
          method:'DELETE',
          headers: {
            'Content-Type':'application/json'
        }
        })
        window.location = "/students"
    }

    return (
        <div className='page'>
             <div className='navigate-buttons'>
            <Link to ={"/students"}>
                <ArrowLeft className="arrow-left-icon" />
            </Link>
            <Link to ={"/"}>
                <HomeIcon className = "home-icon" />
            </Link>
            </div>
        {student!==null&&
            <div className='student'>
                {!editing&&<>
                    {Object.keys(student).slice(1).map((key,index)=>{                    
                        return(
                        <>
                        {headers[index]&&
                        <SingleStudentInfo
                            key = {index}
                            header = {headers[index]}
                            info = {student[key]}
                        />}
                        </>)
                    })}
            
                <span>
                    {convert_date(student.created)}
                </span>
                <br></br>
                
                {(student.created !== student.updated) && 
                    <span>
                        Updated: {convert_date(student.updated)}
                    </span>}
                    <br></br>
                    <UpdateStudents
                        functionality = "edit"
                        props = {student}
                    /> 
                    </>}

                {editing&&<>
                    <button
                        type = "button"
                        onClick={() => setEditing(false)}>
                        Cancel
                    </button>   
                </>} 

                <br></br>
                <button 
                    type="button" 
                    onClick={() =>{removeStudent()}}
                    >Remove
                </button>
            </div>}
        </div>
      )
    }
export default StudentPage
