import React, {useState} from 'react'

const SwitchTheme = () => {

    const [theme, setTheme] = useState('dark')
    
    return (
        <div>
            <button
                onClick={
                    setTheme((theme) => {return theme==='dark'?'light':'dark'})
                    }>

            </button>
            <label>
                Go to {theme==='dark'?'light':'dark'} mode
            </label>
        </div>
    )
}

export default SwitchTheme
