import React, { useState } from 'react'
import UpdateStudents from './UpdateStudents'
import SingleStudentInfo from './SingleStudentInfo'
import DisplayStudentDetails from './DisplayStudentDetails'
const StudentShort = (props) => {
    
    
    const {
        id,
        name,
        salary,
        lessons_time,
        unpaid_time,
        holydays,
        
    } = {...props}

    const headers = ["Student's name", "Salary", 
                     "Lessons days", 
                        "Unpaid teaching time", "Holydays/break"]

    
    


    

  return (
   
    <div className='student'>
      
      <>
        {Object.keys(props).map((key,index)=>{
          return(
            <>
              {headers[index]&&<SingleStudentInfo
                header = {headers[index]}
                info = {props[key]}
              />}
            </>    
          )
        })}
          
        {/* <span>
          {convert_date(created_date)}
        </span>
        <br></br>
        {(created_date !== updated_date) && 
        <span>
          Updated: {convert_date(updated_date)}
        </span>} */}
        <br></br>
        {/* <UpdateStudents
          functionality = "edit"
          props = {props}
        /> */}
      </>

      {/* {editing&&
      <>
        <button
          type = "button"
          onClick={() => setEditing(false)}>
          Cancel
        </button>   
      </>} 

        <br></br>
        <button 
          type="button" 
          onClick={() =>{removeStudent()}}
          >Remove
        </button> */}
        <DisplayStudentDetails
          id = {id}
        />
    </div>
    
  )
}

export default StudentShort
