import React, { useState } from 'react'
import EditStudent from './EditStudent'
const Student = (props) => {
    const [editing, setEditing] = useState(false)
    
    const {
        id,
        name,
        school,
        salary,
        description,
        single_lesson_time,
        lessons_time,
        unpaid_time,
        holydays,
        created_date,
        updated_date
    } = props

    
    const removeStudent = async() =>
    {
        const request = await fetch(`/students/${id}/remove/`,{
          method:'DELETE',
          headers: {
            'Content-Type':'application/json'
        }
        })
        window.location = "/products"

    }

    

    function convert_date(date)
    {
        const year = date.slice(0,4)
        const month = date.slice(5,7)
        const day = date.slice(8,10)
        const oclock = date.slice(11,16 )
        return year + "." + month + "." + day + " " + oclock
    }
  return (
    <div className='product'>
      {!editing &&
      <>
        <span>{name}</span>
        <p>{school}</p>
        <p>{description}</p>
        <p>{salary}</p>
        <p>{description}</p>
        <p>{single_lesson_time}</p>
        <p>{lessons_time}</p>
        <p>{unpaid_time}</p>
        <p>{holydays}</p>

        <span>
          {convert_date(created_date)}
        </span>
        <br></br>
        {(created_date !== updated_date) && 
        <span>
          Updated: {convert_date(updated_date)}
        </span>}
        <br></br>
        <button
          onClick={() => setEditing(true)}>
          Edit
        </button>
      </>}

      {editing&&
      <>
        <EditStudent
          id = {id} 
        />

        <button
          type = "button"
          onClick={() => setEditing(false)}>
          Cancel
        </button>
      
      </>}
       
        
        
        <br></br>
 
        <button 
          type="button" 
          onClick={() =>{removeStudent()}}
          >Remove
        </button>
        
    </div>
  )
}

export default Student
