import React,{useState} from 'react'

const EditStudent = (props) => {

     const {
        id,
        name,
        school,
        salary,
        description,
        single_lesson_time,
        lessons_time,
        unpaid_time,
        holydays,
        created_date,
        updated_date
    } = props.props

    const [new_name, setName] = useState(name)
    const [new_school, setSchool] = useState(school)
    const [new_description, setDescription] = useState(description)
    const [new_salary, setSalary] = useState(salary)
    const [new_single_lesson_time, setSingle_lesson_time] = useState(single_lesson_time)
    const [new_lessons_time, setLessons_time] = useState(lessons_time)
    const [new_unpaid_time, setUnpaid_time] = useState(unpaid_time)
    const [new_holydays, setHolydays] = useState(holydays)
    
    console.log(name)

    const editStudent = async() =>
    {
        const updated_student = {
          id:id,
          name:name,
          school:school,
          body: description,
          salary:salary,  
          single_lesson_time:single_lesson_time,
          lessons_time:lessons_time,
          unpaid_time:unpaid_time,
          holydays:holydays
        }
        
        fetch(`/students/${id}/update/`,
        {
          method:'PUT',
          headers: {
            'Content-Type':'application/json'
          },
          body:JSON.stringify(updated_student)
        })
        window.location = "/students"

    }

    return (
        <div>
            <input 
                className='add-input'
                id="name-input"
               
                value={new_name}
                onChange={(e) => {setName(e.target.value)}}>
            </input>

            <input 
                className='add-input'
                id= "price-input"
               
                value={new_salary}
                onChange={(e) => {setSalary(e.target.value)}}>
            </input>

            <textarea
                className='add-input'
                
                id="description-input"
                value={new_description}
                onChange={(e) => {setDescription(e.target.value)}}>
            </textarea>
            <button
                type = "button"
                onClick={() => editStudent()}>
                Save
            </button>
        </div>
    )
}

export default EditStudent
