import React,{useState} from 'react'

const UpdateStudents = (props) => {

    const functionality = props.functionality
    if(!props) props = {props:{}} 
    
    const {
        id,
        name,
        school,
        salary,
        body,
        single_lesson_time,
        lessons_times,
        unpaid_time,
        holydays,
        created_date,
        updated_date
    } = props.props
    
    
    const [adding, setAdding] = useState(false)
    const [editing, setEditing] = useState(false)

//student props
    const [new_name, setName] = useState(name)
    const [new_school, setSchool] = useState(school)
    const [new_description, setDescription] = useState(body)
    const [new_salary, setSalary] = useState(salary)
    const [new_single_lesson_time, setSingle_lesson_time] = useState(single_lesson_time)
    const [new_lessons_time, setLessons_time] = useState(lessons_times)
    const [new_unpaid_time, setUnpaid_time] = useState(unpaid_time)
    const [new_holydays, setHolydays] = useState(false)
    
    const updateStudents = async(operation) =>
    {   
        const new_student =
        {
            id: id,
            name: new_name,
            school: new_school,
            body: new_description,
            salary: new_salary,
            single_lesson_time: new_single_lesson_time,
            lessons_times : new_lessons_time,
            unpaid_time : new_unpaid_time,
            holydays : new_holydays
        }
        if(operation ==="add")
        {
            const request = await fetch('/student/add/',{
                method: "POST",
                headers: {
                    'Content-Type':'application/json'
                },
                body:JSON.stringify(new_student)
            })
            setAdding(false)
        }
        else if(operation === "edit")
        {
            
            fetch(`/students/${id}/update/`,
            {
            method:'PUT',
            headers: {
                'Content-Type':'application/json'
            },
            body:JSON.stringify(new_student)
        })
            setEditing(false)
        }
        window.location = "/students"
    }
    return (
        <div>  
            {!adding&&functionality === "add" && <button
                id="add-student-button"
                type='button'
                onClick={()=>{setAdding(true)}}>
                +
            </button>}

            {!editing&&functionality === "edit" && <button
                id="edit-student-button"
                type='button'
                onClick={()=>{setEditing(true)}}>
                EDIT
            </button>}

            {(adding || editing )&&<div>
                <form id='add-student-form'>
                    <header>{adding? "Add": "Edit"}</header>
                    <input 
                        className='add-input'
                        id="name-input"
                        placeholder={editing?name:'name'}
                        value={new_name}
                        onChange={(e) => {setName(e.target.value)}}>
                    </input>
                    <input 
                        className='add-input'
                        id="name-input"
                        placeholder={editing?school:'shool and class'}
                        value={new_school}
                        onChange={(e) => {setSchool(e.target.value)}}>
                    </input>
                    <textarea 
                        className='add-input'
                        id="name-input"
                        placeholder={editing?body:'about...'}
                        value={new_description}
                        onChange={(e) => {setDescription(e.target.value)}}>
                    </textarea>
                    <input 
                        className='add-input'
                        id="name-input"
                        placeholder={editing?salary:'Money / hour'}
                        value={new_salary}
                        onChange={(e) => {setSalary(e.target.value)}}>
                    </input>
                    <input 
                        className='add-input'
                        id="name-input"
                        placeholder={editing?lessons_times:'lessons times'}
                        value={new_lessons_time}
                        onChange={(e) => {setLessons_time(e.target.value)}}>
                    </input>
                    <input 
                        className='add-input'
                        id="name-input"
                        placeholder={editing?single_lesson_time:'single lesson time'}
                        value={new_single_lesson_time}
                        onChange={(e) => {setSingle_lesson_time(e.target.value)}}>
                    </input>
                    <input 
                        className='add-input'
                        id="name-input"
                        placeholder={editing?unpaid_time:'unpaid teaching time'}
                        value={new_unpaid_time}
                        onChange={(e) => {setUnpaid_time(e.target.value)}}>
                    </input>
                   <br></br>
                    <input 
                        type = "checkbox"
                        name = "holydays"
                        value={false}
                        onClick={(e) => {setHolydays((prev)=> {return !prev})}}>
                    </input>
                    <label htmlFor='holdays'>on holydays?</label>
                </form>
                <button
                    onClick={() => {adding?setAdding(false):setEditing(false)}}
                    type="button">              
                    Cancel
                </button>
                <button
                    type="button"      
                    onClick= {()=>{adding?updateStudents("add"):updateStudents("edit")}}>
                    Save
                </button>          
            </div>}      
        </div>
    )
}
export default UpdateStudents
