import React,{useState, useEffect} from 'react'
import { useParams } from 'react-router-dom';
import SingleStudentInfo from '../components/SingleStudentInfo';
import UpdateStudents from '../components/UpdateStudents';

const StudentPage = () => {
    const {id} = useParams()
    const [student, setStudent] = useState(null)
    const [editing, setEditing] = useState(false)

    const getStudent = async() => {
        
        const response = await fetch(`/students/${id}`)
        const data = await response.json()
        console.log("d",data)
        setStudent(data)
    }
    useEffect(()=>{
        getStudent()
    },[])

    console.log(student)

    const headers = ["Student's name", "Salary", 
                     "Lessons days", 
                        "Unpaid teaching time", "Holydays/break"]

    function convert_date(date)
    {
        const year = date.slice(0,4)
        const month = date.slice(5,7)
        const day = date.slice(8,10)
        const oclock = date.slice(11,16 )
        return year + "." + month + "." + day + " " + oclock
    }
    const removeStudent = async() =>
    {
        const request = await fetch(`/students/${id}/remove/`,{
          method:'DELETE',
          headers: {
            'Content-Type':'application/json'
        }
        })
        window.location = "/students"
    }

    return (
        <div className='student'>
                {Object.keys(student).map((key,index)=>{
                     return(
                        <>
                        {headers[index]&&<SingleStudentInfo
                            header = {''}
                            info = {student[key]}
                        />}
                        </>)
                })}
            
            <span>
            {convert_date(student.created_date)}
            </span>
            <br></br>
            {(student.created_date !== student.updated_date) && 
            <span>
            Updated: {convert_date(student.updated_date)}
            </span>}
            <br></br>
            <UpdateStudents
                functionality = "edit"
                props = {student}
            /> 
       

       {editing&&
        <>
            <button
            type = "button"
            onClick={() => setEditing(false)}>
            Cancel
            </button>   
        </>} 

            <br></br>
            <button 
            type="button" 
            onClick={() =>{removeStudent()}}
            >Remove
            </button>
        
    </div>
  )
}

export default StudentPage
