import React,{useState} from 'react'

const UpdateStudents = () => {
    const [adding, setAdding] = useState(false)

//student props
    const [new_name, setName] = useState('')
    const [new_school, setSchool] = useState('')
    const [new_description, setDescription] = useState('')
    const [new_salary, setSalary] = useState('')
    const [new_single_lesson_time, setSingle_lesson_time] = useState('')
    const [new_lessons_time, setLessons_time] = useState('')
    const [new_unpaid_time, setUnpaid_time] = useState('')
    const [new_holydays, setHolydays] = useState('')
    
   
    const addProduct = async() =>
    {   
        const new_product =
        {
            name: new_name,
            // price: price,
            // body: description
        }

        const request = await fetch('/students/add/',{
            method: "POST",
            headers: {
                'Content-Type':'application/json'
            },
            body:JSON.stringify(new_product)
        })
        setAdding(false)
        window.location = "/students"
    }

    return (
        <div>
            {!adding&&<button
                id="add-product-button"
                type='button'
                onClick={()=>{setAdding(true)}}>
                +
            </button>}
            {adding&&<div>
                <form id='add-product-form'>
                    <header>Add new student</header>
                    <input 
                        className='add-input'
                        id="name-input"
                        placeholder='name'
                        value={new_name}
                        onChange={(e) => {setName(e.target.value)}}>
                    </input>
                    <input 
                        className='add-input'
                        id="name-input"
                        placeholder='shool and class'
                        value={new_school}
                        onChange={(e) => {setSchool(e.target.value)}}>
                    </input>
                    <textarea 
                        className='add-input'
                        id="name-input"
                        placeholder='about...'
                        value={new_description}
                        onChange={(e) => {setDescription(e.target.value)}}>
                    </textarea>
                    <input 
                        className='add-input'
                        id="name-input"
                        placeholder='Money / hour'
                        value={new_salary}
                        onChange={(e) => {setSalary(e.target.value)}}>
                    </input>
                    <input 
                        className='add-input'
                        id="name-input"
                        placeholder='lessons times'
                        value={new_salary}
                        onChange={(e) => {setLessons_time(e.target.value)}}>
                    </input>
                    <input 
                        className='add-input'
                        id="name-input"
                        placeholder='single lesson time'
                        value={new_salary}
                        onChange={(e) => {setSingle_lesson_time(e.target.value)}}>
                    </input>
                    <input 
                        className='add-input'
                        id="name-input"
                        placeholder='unpaid teaching time'
                        value={new_unpaid_time}
                        onChange={(e) => {setUnpaid_time(e.target.value)}}>
                    </input>
                    <input 
                        type = "checkbox"
                        name = "holydays"
                        className='add-input'
                        id="name-input"
                        placeholder='is on holyday'
                        value={new_holydays}
                        onChange={(e) => {setHolydays(e.target.value)}}>
                    </input>
                    <label htmlFor = "holydays">holydays?</label>


                </form>
                <button
                    onClick={() => {setAdding(false)}}
                    type="button">              
                    Cancel
                </button>
                <button
                    onClick= {()=>{addProduct()}}>
                    Save
                </button>
            </div>}
            
        </div>
    )
}

export default UpdateStudents
