import React,{useState} from 'react'
import {ReactComponent as StatsIcon}from '../icons/stats_icon.svg'
import {ReactComponent as Xicon} from '../icons/x-icon.svg'

const Stats = (props) => {
    const {students} = props
    const [state,setState] = useState(false)
    const[pending, setPending] = useState(0)

   
    const getStats = () => {
        if(students.length) 
        {
            console.log(students)
            students.forEach((student,index) => {
                console.log(student)
                // setPending((prevPending)=>{
                //     return prevPending + student.unpaid_time/60 * student.salary
                // })
    
            });
        }
    }

    getStats()

    return (
        <div id='stats-container'>
            {!state && <StatsIcon 
                            id='stats-icon'
                            className='icon'
                            onClick={()=>{setState(true)}}        
                        />}
            {state && students.length && 
            <div 
                id='stats-list'>
                <p className='stats-info'>
                    Pending payment: {pending} zł
                </p>
                <p className='stats-info'>
                    Students amount: {}
                </p>
                <p className='stats-info'>
                    All lessons time: {} h/week
                </p>
                <p className='stats-info'>
                    Salary per week: {} zł
                </p>
                
                <Xicon 
                    className='icon' 
                    onClick={()=>{setState(false)}}
                    id='x-icon' 
                /> 
            </div>}
        </div>
        
    )
}

export default Stats
