import React,{useState} from 'react'

const EditStudent = (props) => {

    const id = props.id
    const [name, setName] = useState('')
    const [school, setSchool] = useState('')
    const [description, setDescription] = useState('')
    const [salary, setSalary] = useState('')
    const [single_lesson_time, setSingle_lesson_time] = useState('')
    const [lessons_time, setLessons_time] = useState('')
    const [unpaid_time, setUnpaid_time] = useState('')
    const [holydays, setHolydays] = useState('')
    
    

    const editStudent = async() =>
    {
        const updated_student = {
          id:id,
          name:name,
          school:school,
          body: description,
          salary:salary,  
          single_lesson_time:single_lesson_time,
          lessons_time:lessons_time,
          unpaid_time:unpaid_time,
          holydays:holydays
        }
        
        fetch(`/students/${id}/update/`,
        {
          method:'PUT',
          headers: {
            'Content-Type':'application/json'
          },
          body:JSON.stringify(updated_student)
        })
        window.location = "/students"

    }

    return (
        <div>
            <input 
                className='add-input'
                id="name-input"
               
                value={name}
                onChange={(e) => {setName(e.target.value)}}>
            </input>

            <input 
                className='add-input'
                id= "price-input"
               
                value={salary}
                onChange={(e) => {setSalary(e.target.value)}}>
            </input>

            <textarea
                className='add-input'
                
                id="description-input"
                value={description}
                onChange={(e) => {setDescription(e.target.value)}}>
            </textarea>
            <button
                type = "button"
                onClick={() => editStudent()}>
                Save
            </button>
        </div>
    )
}

export default EditStudent
