import React,{useState} from 'react'

const AddButton = () => {
    const [adding, setAdding] = useState(false)
    
    function addProduct()
    {
        setAdding(true)
    }

    return (
        <div>
            {!adding&&<button
                id="add-product-button"
                type='button'
                onClick={()=>{addProduct()}}>
                +
            </button>}

            
            {adding&&<div>
                <form>
                    <input></input>
                    <input></input>
                    <textarea></textarea>
                </form>
            </div>}
            
        </div>
    )
}

export default AddButton
