import React, { useState } from 'react'


const Product = (props) => {
    const [editing, setEditing] = useState(false)
    
    const {
        id,
        name,
        price,
        description,
        created_date,
        updated_date
    } = props

    const [editedName, setEditedName] = useState(name)
    const [editedPrice, setEditedPrice] = useState(price)
    const [editedDescription, setEditedDescription] = useState(description)
    console.log(editedName,editedPrice,editedDescription)
    const removeProduct = async() =>
    {
        const request = await fetch(`/products/${id}/remove/`,{
          method:'DELETE',
          headers: {
            'Content-Type':'application/json'
        }
        })
        window.location = "/products"

    }

    const editProduct = async() =>
    {
        console.log("safe")
        const updated_product = {
          name:editedName,
          price: editedPrice,
          body: editedDescription
        }
        console.log(updated_product)
        fetch(`/products/${id}/update/`,
        {
          method:'PUT',
          headers: {
            'Content-Type':'application/json'
          },
          body:JSON.stringify(updated_product)
        })
        window.location = "/products"

    }

    function convert_date(date)
    {
        const year = date.slice(0,4)
        const month = date.slice(5,7)
        const day = date.slice(8,10)
        const oclock = date.slice(11,16 )
        return year + "." + month + "." + day + " " + oclock
    }
  return (
    <div className='product'>
      {!editing &&
      <>
        <span>{name}</span>
        <p>{description}</p>
        <p>{price}</p>

        <span>
          {convert_date(created_date)}
        </span>
        {(created_date !== updated_date) && 
        <span>
          Updated: {convert_date(updated_date)}
        </span>}
        <br></br>
        <button
          onClick={() => setEditing(true)}>
          Edit
        </button>
      </>}

      {editing&&
      <>
        <input 
            className='add-input'
            id="name-input"
            placeholder='name'
            value={editedName}
            onChange={(e) => {setEditedName(e.target.value)}}>
        </input>

        <input 
            className='add-input'
            id= "price-input"
            placeholder='price'
            value={editedPrice}
            onChange={(e) => {setEditedPrice(e.target.value)}}>
        </input>

        <textarea
            className='add-input'
            placeholder='Description'
            id="description-input"
            value={editedDescription}
            onChange={(e) => {setEditedDescription(e.target.value)}}>
        </textarea>
        <button
          type = "button"
          onClick={() => editProduct()}>
          Save
        </button>
        <button
          type = "button"
          onClick={() => setEditing(false)}>
          Cancel
        </button>
      
      </>}
       
        
        
        <br></br>
 
        <button 
          type="button" 
          onClick={() =>{removeProduct()}}
          >Remove
        </button>
        
    </div>
  )
}

export default Product
