function switchTheme(theme)
{
    console.log(1)
    const button = document.querySelector('#change-theme-button')
    if(button !== null)
    {
        
        button.addEventListener('click', () =>
        {
            if(theme === 'light') console.log('li')//document.body.classList.toggle('dark')
            else console.log('d')//document.body.classList.toggle('light')
        })
    }
    
}

export default switchTheme
