import React, { useState } from 'react'
import UpdateStudents from './UpdateStudents'
import SingleStudentInfo from './SingleStudentInfo'
const Student = (props) => {
    const [editing, setEditing] = useState(false)
    
    const {
        id,
        name,
        school,
        salary,
        description,
        single_lesson_time,
        lessons_time,
        unpaid_time,
        holydays,
        created_date,
        updated_date
    } = {...props}

    const headers = ["Student's name", "Class", "Salary", 
                    "About", "Lesson time", "Lessons days", 
                        "Unpaid teaching time", "Holydays/break"]

    
    const removeStudent = async() =>
    {
        const request = await fetch(`/students/${id}/remove/`,{
          method:'DELETE',
          headers: {
            'Content-Type':'application/json'
        }
        })
        window.location = "/students"
    }


    function convert_date(date)
    {
        const year = date.slice(0,4)
        const month = date.slice(5,7)
        const day = date.slice(8,10)
        const oclock = date.slice(11,16 )
        return year + "." + month + "." + day + " " + oclock
    }

  return (
    <div className='product'>
      {!editing &&
      <>
        {Object.keys(props).map((key,index)=>{
          return(
            <SingleStudentInfo
              header = {headers[index]}
              info = {props[key]}
            />
          )
        })}
          
        <span>
          {convert_date(created_date)}
        </span>
        <br></br>
        {(created_date !== updated_date) && 
        <span>
          Updated: {convert_date(updated_date)}
        </span>}
        <br></br>
        <UpdateStudents
          functionality = "edit"
          props = {props}
        />
      </>}

      {editing&&
      <>
        <button
          type = "button"
          onClick={() => setEditing(false)}>
          Cancel
        </button>   
      </>} 

        <br></br>
        <button 
          type="button" 
          onClick={() =>{removeStudent()}}
          >Remove
        </button>
    </div>
  )
}

export default Student
