const EditStudent = (props) => {

     

    const [new_name, setName] = useState(props.name)
    const [new_school, setSchool] = useState(props.salary)
    const [new_description, setDescription] = useState(props.description)
    const [new_salary, setSalary] = useState(props.salary)
    const [new_single_lesson_time, setSingle_lesson_time] = useState(props.single_lesson_time)
    const [new_lessons_time, setLessons_time] = useState(props.lessons_time)
    const [new_unpaid_time, setUnpaid_time] = useState(props.unpaid_time)
    const [new_holydays, setHolydays] = useState(props.holydays)
    
    console.log(props, new_name)

