                className='add-input'
                id= "price-input"
               
                value={new_salary}
                onChange={(e) => {setSalary(e.target.value)}}>
            </input>

            <textarea
                className='add-input'
                
                id="description-input"
                value={new_description}
                onChange={(e) => {setDescription(e.target.value)}}>
            </textarea>
            <button
                type = "button"
                onClick={() => editStudent()}>
                Save
            </button>
        </div>
    )
}

