import React from 'react'
import EditProduct from './EditProduct'

const Product = (props) => {
    const {
        id,
        name,
        price,
        description,
        created_date,
        updated_date
    } = props

    
    const removeProduct = async() =>
    {
        const request = await fetch(`/products/${id}/remove/`,{
          method:'DELETE',
          headers: {
            'Content-Type':'application/json'
        }
        })
        window.location = "/products"

    }

    function convert_date(date)
    {
        const year = date.slice(0,4)
        const month = date.slice(5,7)
        const day = date.slice(8,10)
        const oclock = date.slice(11,16 )
        return year + "." + month + "." + day + " " + oclock
    }
  return (
    <div className='product'>
        <span>{name}</span>
        <p>{description}</p>
        <p>{price}</p>
        <span>{convert_date(created_date)}</span>
        {(created_date !== updated_date) && <span>
          Updated: {convert_date(updated_date)}
          </span>}
        <br></br>
        <button 
          type="button" 
          onClick={() =>{removeProduct()}}
          >Remove
        </button>
        <br></br>
        <EditProduct/>
        
    </div>
  )
}

export default Product
