import React,{useState} from 'react'

const AddButton = () => {
    const [adding, setAdding] = useState(false)

    function addProduct()
    {
        setAdding(true)
    }

    return (
        <div>
            {adding&&<button
                id="add-product-button"
                type='button'
                onClick={()=>{addProduct()}}>
                +
            </button>}
        </div>
    )
}

export default AddButton
